// src/pages/Quiz.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import quizData from '../data/quizData.json'; // Import the quiz data

const Quiz = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedLevel, setSelectedLevel] = useState(null);
  const [questions, setQuestions] = useState([]);

  const categories = Object.keys(quizData); // Dynamically get categories

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSelectedLevel(null); // Reset level when category changes
  };

  const handleLevelSelect = (level) => {
    setSelectedLevel(level);
  };

  useEffect(() => {
    if (selectedCategory && selectedLevel) {
      setQuestions(quizData[selectedCategory][selectedLevel]);
    }
  }, [selectedCategory, selectedLevel]);

  return (
    <div>
      <Header />
      <main>
        <h2>Quiz Page</h2>
        {!selectedCategory ? (
          <div>
            <h3>Select a Category</h3>
            <ul>
              {categories.map((category) => (
                <li key={category}>
                  <button onClick={() => handleCategorySelect(category)}>
                    {category}
                  </button>
                </li>
              ))}
            </ul>
          </div>
        ) : !selectedLevel ? (
          <div>
            <h3>Select a Level for {selectedCategory}</h3>
            <ul>
              {Object.keys(quizData[selectedCategory]).map((level) => (
                <li key={level}>
                  <button onClick={() => handleLevelSelect(level)}>
                    {level}
                  </button>
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <div>
            <h3>{selectedCategory} - {selectedLevel} Quiz</h3>
            {questions.length > 0 ? (
              <ul>
                {questions.map((question, index) => (
                  <li key={index}>
                    <h3>{question.question}</h3>
                    <ul>
                      {question.options.map((option, optionIndex) => (
                        <li key={optionIndex}>
                          <input
                            type="radio"
                            name={`question-${index}`}
                            id={`answer-${optionIndex}`}
                          />
                          <label htmlFor={`answer-${optionIndex}`}>
                            {option}
                          </label>
                        </li>
                      ))}
                    </ul>
                    </li>
                ))}
              </ul>
            ) : (
              <p>Loading...</p>
            )}
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
};

export default Quiz;
